import React, { useEffect, useRef } from 'react';
import { SigmaContainer, useSigma } from '@react-sigma/core';
import Graph from 'graphology';

const nodes = [
  { id: 'n1', label: 'Ella Fitzgerald: 1917' },
  { id: 'n2', label: 'Frank Sinatra: 1915' },
  { id: 'n3', label: 'Billie Holiday: 1915' },
  { id: 'n4', label: 'Louis Armstrong: 1901' },
  { id: 'n5', label: 'Nina Simone: 1933' },
  { id: 'n6', label: 'Nat King Cole: 1919' },
  { id: 'n7', label: 'Gregory Porter: 1971' },
  { id: 'n8', label: 'Sarah Vaughan: 1924' },
  { id: 'n9', label: 'Michael Bublé: 1975' }
];

const edges = [
  { source: 'n1', target: 'n2' },
  { source: 'n1', target: 'n3' },
  { source: 'n1', target: 'n6' },
  { source: 'n2', target: 'n1' },
  { source: 'n3', target: 'n1' },
  { source: 'n5', target: 'n8' },
  { source: 'n7', target: 'n9' }
];

const GraphComponent = () => {
  const sigma = useSigma();
  const containerRef = useRef(null);

  useEffect(() => {
    const graph = new Graph();
    
    // Add nodes
    nodes.forEach(node => {
      graph.addNode(node.id, {
        label: node.label,
        size: 20,
        color: 'black'
      });
    });

    // Add edges
    edges.forEach(edge => {
      graph.addEdge(edge.source, edge.target);
    });

    // Calculate positions for child nodes of 'n1'
    const parentNode = 'n1';
    const childNodes = ['n2', 'n3', 'n4', 'n5', 'n6', 'n7', 'n8']; // Direct children of 'n1'
    const angleSpread = Math.PI / 2; // Spread across 90 degrees (π/2 radians)
    const radius = 100; // Distance from the parent node
    const centerX = 0; // X position of 'n1'
    const centerY = 0; // Y position of 'n1'
    const startAngle = Math.PI / 4; // Start angle (45 degrees)

    // Set position for parent node
    graph.setNodeAttribute(parentNode, 'x', centerX);
    graph.setNodeAttribute(parentNode, 'y', centerY);

    // Position child nodes
    childNodes.forEach((nodeId, index) => {
      const angle = startAngle + (index * angleSpread) / (childNodes.length - 1);
      const x = centerX + radius * Math.cos(angle);
      const y = centerY + radius * Math.sin(angle);
      graph.setNodeAttribute(nodeId, 'x', x);
      graph.setNodeAttribute(nodeId, 'y', y);
    });

    if (sigma) {
      sigma.setGraph(graph);
      sigma.refresh();
    }
  }, [sigma]);

  return <div ref={containerRef} />;
};

const GraphWrapper = () => (
  <SigmaContainer>
    <GraphComponent />
  </SigmaContainer>
);

export default GraphWrapper;
