import React, { useEffect, useRef, useState } from 'react';
import { SigmaContainer, useSigma } from '@react-sigma/core';
import Graph from 'graphology';
import forceAtlas2, { ForceAtlas2SynchronousLayoutParameters } from "graphology-layout-forceatlas2";
import { useLayoutFactory } from "@react-sigma/layout-core";

async function fetchDataForKey(key) {
  const baseUrl = 'http://localhost:5000';
  const url = `${baseUrl}/get/${key}`;

  try {
    const response = await fetch(url);
    if (!response.ok) {
      throw new Error(`HTTP error! Status: ${response.status}`);
    }
    const data = await response.json();
    return data;
  } catch (error) {
    console.error('Error fetching data:', error);
    throw error;
  }
};

async function getNodeData(nodeID) {
  try {
    const data = await fetchDataForKey(nodeID);

    const center = data.center.map(({ id, label, scopenote }) => ({
      id: id,
      label: label,
      scopenote: scopenote
    }));

    const parents = data.parents.map(({ id, label, scopenote }) => ({
      id: id,
      label: label,
      scopenote: scopenote
    }));

    const children = data.children.map(({ id, label, scopenote }) => ({
      id: id,
      label: label,
      scopenote: scopenote
    }));

    return { center, parents, children };
  } catch (error) {
    console.error('Error fetching node data:', error);
    throw error; // Re-throw the error if you want to handle it further up the call stack
  }
}




const GraphComponent = () => {
  const sigma = useSigma();
  const containerRef = useRef(null);

  useEffect(() =>{

    const graph = new Graph();

    nodes.forEach(node => {
      graph.addNode(node.id, {
        label: node.label,
        size: node.size,
        color: node.color
      });
    });

    edges.forEach(edge => {
     graph.addEdge(edge.source, edge.target);
    });
  
  }, [sigma]);
  return <div ref={containerRef}  />
}


const GraphWrapper = () => (
    <SigmaContainer>
      <GraphComponent />
    </SigmaContainer>
  );
  
export default GraphWrapper;
