import React, { useEffect, useRef, useState } from 'react';
import { SigmaContainer, useSigma } from '@react-sigma/core';
import Graph from 'graphology';

// Define the central node
const centralNode = { id: 'Root', label: 'Root' };

async function fetchDataForKey(key) {
  const baseUrl = 'http://localhost:5000';
  const url = `${baseUrl}/get/${key}`;

  try {
    const response = await fetch(url);

    if (!response.ok) {
      throw new Error(`HTTP error! Status: ${response.status}`);
    }

    const data = await response.json();
    return data;
  } catch (error) {
    console.error('Error fetching data:', error);
    throw error; // Re-throw the error for further handling if needed
  }
}

const rootId = centralNode.id;

function defineEdges(data) {
  
  const edges = [];

  // Create edges from children to 'Root'
  data.children.forEach((childId) => {
    edges.push({ source: childId, target: rootId });
  });

  // Create edges from 'Root' to parents
  data.parents.forEach((parentId) => {
    edges.push({ source: rootId, target: parentId });
  });

  return edges;
}

const GraphComponent = () => {
  const sigma = useSigma();
  const containerRef = useRef(null);
  const [parentNodes, setParentNodes] = useState([]);
  const [childNodes, setChildNodes] = useState([]);
  const [edges, setEdges] = useState([]);

  useEffect(() => {
    fetchDataForKey(rootId)
      .then((data) => {
        // Transform the response data into the desired format for parentNodes and childNodes
        const parents = data.parents.map((id) => ({
          id: id,
          label: id // Using the ID as the label for now
        }));

        const children = data.children.map((id) => ({
          id: id,
          label: id // Using the ID as the label for now
        }));

        setParentNodes(parents);
        setChildNodes(children);
        setEdges(defineEdges(data));
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  }, []);

  useEffect(() => {
    if (sigma && parentNodes.length > 0 && childNodes.length > 0 && edges.length > 0) {
      const graph = new Graph();
      
      // Add central node
      graph.addNode(centralNode.id, {
        label: centralNode.label,
        size: 20,
        color: 'black'
      });

      // Add parent nodes
      parentNodes.forEach(node => {
        graph.addNode(node.id, {
          label: node.label,
          size: 20,
          color: 'black'
        });
      });

      // Add child nodes
      childNodes.forEach(node => {
        graph.addNode(node.id, {
          label: node.label,
          size: 20,
          color: 'black'
        });
      });

      // Add edges
      edges.forEach(edge => {
        graph.addEdge(edge.source, edge.target);
      });

      // Central node position
      const centerX = 0;
      const centerY = 0;
      graph.setNodeAttribute(centralNode.id, 'x', centerX);
      graph.setNodeAttribute(centralNode.id, 'y', centerY);

      // Parent nodes (behind n1)
      const parentAngleSpread = Math.PI / 2; 
      const parentRadius = 100; 
      const parentStartAngle = 3 * Math.PI / 4; 

      parentNodes.forEach((node, index) => {
        const angle = parentStartAngle + (index * parentAngleSpread) / (parentNodes.length - 1);
        const x = centerX + parentRadius * Math.cos(angle);
        const y = centerY + parentRadius * Math.sin(angle);
        graph.setNodeAttribute(node.id, 'x', x);
        graph.setNodeAttribute(node.id, 'y', y);
      });

      // Child nodes (in front of n1)
      const childAngleSpread = Math.PI / 2; 
      const childRadius = 100; 
      const childStartAngle = 7 * Math.PI / 4;

      childNodes.forEach((node, index) => {
        const angle = childStartAngle + (index * childAngleSpread) / (childNodes.length - 1);
        const x = centerX + childRadius * Math.cos(angle);
        const y = centerY + childRadius * Math.sin(angle);
        graph.setNodeAttribute(node.id, 'x', x);
        graph.setNodeAttribute(node.id, 'y', y);
      });

      sigma.setGraph(graph);
      sigma.refresh();
    }
  }, [sigma, parentNodes, childNodes, edges]);

  return <div ref={containerRef} />;
};

const GraphWrapper = () => (
  <SigmaContainer>
    <GraphComponent />
  </SigmaContainer>
);

export default GraphWrapper;
