import React, { useEffect, useRef } from 'react';
import { SigmaContainer, useSigma } from '@react-sigma/core';
import Graph from 'graphology';
import circularLayout from 'graphology-layout/circular';

const nodes = [
  { id: 'n1', label: 'Ella Fitzgerald: 1917', size: 25, color: 'red' },
  { id: 'n2', label: 'Frank Sinatra: 1915', size: 25, color: 'red' },
  { id: 'n3', label: 'Billie Holiday: 1915', size: 25, color: 'red' },
  { id: 'n4', label: 'Louis Armstrong: 1901', size: 25, color: 'yellow' },
  { id: 'n5', label: 'Nina Simone: 1933', size: 25, color: 'orange' },
  { id: 'n6', label: 'Nat King Cole: 1919', size: 25, color: 'red' },
  { id: 'n7', label: 'Gregory Porter: 1971', size: 25, color: 'teal' },
  { id: 'n8', label: 'Sarah Vaughan: 1924', size: 25, color: 'orange' },
  { id: 'n9', label: 'Michael Bublé: 1975', size: 25, color: 'teal' }
];

const edges = [
  { source: 'n1', target: 'n2' },
  { source: 'n1', target: 'n3' },
  { source: 'n1', target: 'n6' },
  { source: 'n2', target: 'n1' },
  { source: 'n3', target: 'n1' },
  { source: 'n5', target: 'n8' },
  { source: 'n7', target: 'n9' }
];

const GraphComponent = () => {
    const sigma = useSigma();
    const containerRef = useRef(null);

    useEffect(() => {
        const graph = new Graph();
        // Add nodes
        nodes.forEach(node => {
          graph.addNode(node.id, {
            label: node.label,
            size: 25,
            color: node.color
          });
        });

        // Add edges - GROUPED / LINKED DECADES
        edges.forEach(edge => {
          graph.addEdge(edge.source, edge.target);
        });

    // Apply a circular layout
    circularLayout.assign(graph);
    if (sigma) {
        sigma.setGraph(graph);
        sigma.refresh();
      }
    }, [sigma]);
  
    return <div ref={containerRef}  />;
  };

  const GraphWrapper = () => (
    <SigmaContainer>
      <GraphComponent />
    </SigmaContainer>
  );
  
  export default GraphWrapper;
