import React, { useEffect, useRef } from 'react';
import { SigmaContainer, useSigma } from '@react-sigma/core';
import Graph from 'graphology';

// Define the central node
const centralNode = { id: 'n1', label: 'Ella Fitzgerald: 1917' };

// Define all other nodes as child nodes
const otherNodes = [
  { id: 'n2', label: 'n2 Sinatra: 1915' },
  { id: 'n3', label: 'n3 Holiday: 1915' },
  { id: 'n4', label: 'n4 Armstrong: 1901' },
  { id: 'n5', label: 'n5 Simone: 1933' },
  { id: 'n6', label: 'n6 King Cole: 1919' },
  { id: 'n7', label: 'n7 Porter: 1971' },
  { id: 'n8', label: 'n8 Vaughan: 1924' },
  { id: 'n9', label: 'n9 Bublé: 1975' }
];

const edges = [
  { source: 'n1', target: 'n2' },
  { source: 'n1', target: 'n3' },
  { source: 'n1', target: 'n4' },
  { source: 'n1', target: 'n5' },
  { source: 'n1', target: 'n6' },
  { source: 'n1', target: 'n7' },
  { source: 'n1', target: 'n8' },
  { source: 'n1', target: 'n9' }
];

const GraphComponent = () => {
  const sigma = useSigma();
  const containerRef = useRef(null);

  useEffect(() => {
    const graph = new Graph();
    
    // Add central node
    graph.addNode(centralNode.id, {
      label: centralNode.label,
      size: 20,
      color: 'black'
    });

    // Add other nodes
    otherNodes.forEach(node => {
      graph.addNode(node.id, {
        label: node.label,
        size: 20,
        color: 'black'
      });
    });

    // Add edges
    edges.forEach(edge => {
      graph.addEdge(edge.source, edge.target);
    });

    // Central node position
    const centerX = 0;
    const centerY = 0;
    graph.setNodeAttribute(centralNode.id, 'x', centerX);
    graph.setNodeAttribute(centralNode.id, 'y', centerY);

    // Position all other nodes in a circle around the central node
    const radius = 150; // Distance from the central node
    const totalNodes = otherNodes.length;
    const angleIncrement = (2 * Math.PI) / totalNodes; // Full circle divided by number of nodes

    otherNodes.forEach((node, index) => {
      const angle = index * angleIncrement;
      const x = centerX + radius * Math.cos(angle);
      const y = centerY + radius * Math.sin(angle);
      graph.setNodeAttribute(node.id, 'x', x);
      graph.setNodeAttribute(node.id, 'y', y);
    });

    if (sigma) {
      sigma.setGraph(graph);
      sigma.refresh();
    }
  }, [sigma]);

  return <div ref={containerRef} />;
};

const GraphWrapper = () => (
  <SigmaContainer>
    <GraphComponent />
  </SigmaContainer>
);

export default GraphWrapper;
