import React, { useEffect, useRef, useState } from 'react';
import { SigmaContainer, useSigma } from '@react-sigma/core';
import Graph from 'graphology';

// Fetch data for a given key
async function fetchDataForKey(key) {
  const baseUrl = 'http://localhost:5000';
  const url = `${baseUrl}/get/${key}`;

  try {
    const response = await fetch(url);
    if (!response.ok) {
      throw new Error(`HTTP error! Status: ${response.status}`);
    }
    const data = await response.json();
    return data;
  } catch (error) {
    console.error('Error fetching data:', error);
    throw error;
  }
}

// Define edges based on fetched data
function defineEdges(data, centralNodeId) {
  const edges = [];

  data.children.forEach((childId) => {
    edges.push({ source: childId, target: centralNodeId });
  });

  data.parents.forEach((parentId) => {
    edges.push({ source: centralNodeId, target: parentId });
  });

  return edges;
}

const GraphComponent = () => {
  const sigma = useSigma();
  const containerRef = useRef(null);

  // Use state for the central node
  const [centralNode, setCentralNode] = useState({ id: 'Root', label: 'Root' });
  const [parentNodes, setParentNodes] = useState([]);
  const [childNodes, setChildNodes] = useState([]);
  const [edges, setEdges] = useState([]);

  // Fetch data when central node changes
  useEffect(() => {
    fetchDataForKey(centralNode.id)
      .then((data) => {
        const parents = data.parents.map((id) => ({ id, label: id }));
        const children = data.children.map((id) => ({ id, label: id }));

        setParentNodes(parents);
        setChildNodes(children);
        setEdges(defineEdges(data, centralNode.id));
      })
      .catch((error) => console.error('Error:', error));
  }, [centralNode]);

  // Set up graph when nodes or edges change
  useEffect(() => {
    if (sigma && parentNodes.length > 0 && childNodes.length > 0 && edges.length > 0) {
      const graph = new Graph();

      // Add central node
      graph.addNode(centralNode.id, {
        label: centralNode.label,
        size: 20,
        color: 'black',
      });

      parentNodes.forEach((node) => {
        graph.addNode(node.id, { label: node.label, size: 20, color: 'black' });
      });

      childNodes.forEach((node) => {
        graph.addNode(node.id, { label: node.label, size: 20, color: 'black' });
      });

      edges.forEach((edge) => {
        graph.addEdge(edge.source, edge.target);
      });

      sigma.setGraph(graph);
      sigma.refresh();

      // Add click listener for nodes
      sigma.getGraph().on('clickNode', (event) => {
        const clickedNodeId = event.node;
        const clickedNodeLabel = sigma.getGraph().getNodeAttribute(clickedNodeId, 'label');
        
        // Update the central node with clicked node's details
        setCentralNode({ id: clickedNodeId, label: clickedNodeLabel });
      });
    }
  }, [sigma, parentNodes, childNodes, edges, centralNode]);

  return <div ref={containerRef} />;
};

const GraphWrapper = () => (
  <SigmaContainer>
    <GraphComponent />
  </SigmaContainer>
);

export default GraphWrapper;
