import React, { useEffect, useRef, useState } from 'react';
import { SigmaContainer, useSigma } from '@react-sigma/core';
import Graph from 'graphology';

async function fetchDataForKey(key) {
  const baseUrl = 'http://localhost:5000';
  const url = `${baseUrl}/get/${key}`;

  try {
    const response = await fetch(url);
    if (!response.ok) {
      throw new Error(`HTTP error! Status: ${response.status}`);
    }
    const data = await response.json();
    return data;
  } catch (error) {
    console.error('Error fetching data:', error);
    throw error;
  }
}

function defineEdges(data, centralNodeId) {
  const edges = [];
  data.children.forEach((child) => {
    edges.push({ source: child.id, target: centralNodeId });
  });
  data.parents.forEach((parent) => {
    edges.push({ source: centralNodeId, target: parent.id });
  });
  return edges;
}

const GraphComponent = () => {
  const sigma = useSigma();
  const containerRef = useRef(null);
  const [centralNode, setCentralNode] = useState({ id: 'Root', label: 'Root' });
  const [parentNodes, setParentNodes] = useState([]);
  const [childNodes, setChildNodes] = useState([]);
  const [edges, setEdges] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  // Function to handle node clicks
  const handleNodeClick = async (nodeId) => {
    setIsLoading(true);
    try {
      // Fetch data for the clicked node first
      const newData = await fetchDataForKey(nodeId);
      
      // Create the new central node object
      const newCentralNode = {
        id: nodeId,
        label: nodeId // You might want to get this from newData if available
      };

      // Update the central node and trigger a re-render
      setCentralNode(newCentralNode);

      // Transform and set the new parent and child nodes
      const parents = newData.parents.map((parent) => ({
        id: parent.id,
        label: parent.label || parent.id
      }));

      const children = newData.children.map((child) => ({
        id: child.id,
        label: child.label || child.id
      }));

      setParentNodes(parents);
      setChildNodes(children);
      setEdges(defineEdges(newData, nodeId));
    } catch (error) {
      console.error('Error handling node click:', error);
    } finally {
      setIsLoading(false);
    }
  };

  // Effect to update graph visualization
  useEffect(() => {
    if (isLoading) return;

    // Create a new graph instance
    const graph = new Graph();
    
    // Add central node
    graph.addNode(centralNode.id, {
      label: centralNode.label,
      size: 20,
      color: 'black'
    });

    // Add parent nodes
    parentNodes.forEach(node => {
      if (!graph.hasNode(node.id)) {
        graph.addNode(node.id, {
          label: node.label,
          size: 20,
          color: 'blue'
        });
      }
    });

    // Add child nodes
    childNodes.forEach(node => {
      if (!graph.hasNode(node.id)) {
        graph.addNode(node.id, {
          label: node.label,
          size: 20,
          color: 'green'
        });
      }
    });

    // Add edges
    edges.forEach(edge => {
      if (!graph.hasEdge(edge.source, edge.target)) {
        try {
          graph.addEdge(edge.source, edge.target);
        } catch (error) {
          console.warn(`Failed to add edge from ${edge.source} to ${edge.target}:`, error);
        }
      }
    });

    // Position nodes
    const centerX = 0;
    const centerY = 0;
    graph.setNodeAttribute(centralNode.id, 'x', centerX);
    graph.setNodeAttribute(centralNode.id, 'y', centerY);

    // Position parent nodes
    const parentAngleSpread = Math.PI / 2;
    const parentRadius = 100;
    const parentStartAngle = 3 * Math.PI / 4;

    parentNodes.forEach((node, index) => {
      const angle = parentStartAngle + (index * parentAngleSpread) / Math.max(1, parentNodes.length - 1);
      const x = centerX + parentRadius * Math.cos(angle);
      const y = centerY + parentRadius * Math.sin(angle);
      graph.setNodeAttribute(node.id, 'x', x);
      graph.setNodeAttribute(node.id, 'y', y);
    });

    // Position child nodes
    const childAngleSpread = Math.PI / 2;
    const childRadius = 100;
    const childStartAngle = 7 * Math.PI / 4;

    childNodes.forEach((node, index) => {
      const angle = childStartAngle + (index * childAngleSpread) / Math.max(1, childNodes.length - 1);
      const x = centerX + childRadius * Math.cos(angle);
      const y = centerY + childRadius * Math.sin(angle);
      graph.setNodeAttribute(node.id, 'x', x);
      graph.setNodeAttribute(node.id, 'y', y);
    });

    // Clear any existing click handlers
    sigma.removeAllListeners('clickNode');
    
    // Set up click handler
    sigma.on('clickNode', (event) => {
      handleNodeClick(event.node);
    });

    // Clear the existing graph and set the new one
    sigma.setGraph(graph);
    sigma.refresh();

    // Cleanup function
    return () => {
      sigma.removeAllListeners('clickNode');
    };
  }, [sigma, parentNodes, childNodes, edges, centralNode, isLoading]);

  // Initial data load
  useEffect(() => {
    handleNodeClick(centralNode.id);
  }, []); // Only run on mount

  return <div ref={containerRef} />;
};

const GraphWrapper = () => (
  <SigmaContainer>
    <GraphComponent />
  </SigmaContainer>
);

export default GraphWrapper;
