import React, { useEffect, useRef, useState } from 'react';
import { SigmaContainer, useSigma } from '@react-sigma/core';
import Graph from 'graphology';

// CSS for fade-in effect
const fadeInStyle = `
  .fade-in {
    opacity: 0;
    animation: fadeIn 1s forwards;
  }

  @keyframes fadeIn {
    to {
      opacity: 1;
    }
  }
`;

// Inject the CSS into the document
const styleSheet = document.createElement("style");
styleSheet.type = "text/css";
styleSheet.innerText = fadeInStyle;
document.head.appendChild(styleSheet);

async function fetchDataForKey(key) {
  const baseUrl = 'http://localhost:5000';
  const url = `${baseUrl}/get/${key}`;

  try {
    const response = await fetch(url);
    if (!response.ok) {
      throw new Error(`HTTP error! Status: ${response.status}`);
    }
    const data = await response.json();
    return data;
  } catch (error) {
    console.error('Error fetching data:', error);
    throw error;
  }
}

function defineEdges(data, centralNodeId) {
  const edges = [];
  data.children.forEach((child) => {
    edges.push({ source: child.id, target: centralNodeId });
  });
  data.parents.forEach((parent) => {
    edges.push({ source: centralNodeId, target: parent.id });
  });
  return edges;
}

const GraphComponent = () => {
  let centralNodeLabel = 'Root';
  const sigma = useSigma();
  const containerRef = useRef(null);
  const [centralNode, setCentralNode] = useState({ id: 'Root', label: 'Root' });
  const [parentNodes, setParentNodes] = useState([]);
  const [childNodes, setChildNodes] = useState([]);
  const [edges, setEdges] = useState([]);

  // Function to get the label by node ID
  function getNodeLabelById(nodeId, nodes) {
    const node = nodes.find((node) => node.id === nodeId);
    return node ? node.label : null;
  }

  // Function to handle node clicks
  const handleNodeClick = (nodeId) => {
    centralNodeLabel = getNodeLabelById(nodeId, [...parentNodes, ...childNodes]);
    setCentralNode({ id: nodeId, label: centralNodeLabel });
  };

  // Effect to fetch data when central node changes
  useEffect(() => {
    if (!centralNode.id) return; // Ensure there's an ID

    fetchDataForKey(centralNode.id)
      .then((data) => {
        const parents = data.parents.map(({ id, label }) => ({
          id: id,
          label: label
        }));

        const children = data.children.map(({ id, label }) => ({
          id: id,
          label: label
        }));

        setParentNodes(parents);
        setChildNodes(children);
        setEdges(defineEdges(data, centralNode.id));
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  }, [centralNode.id]);

  // Effect to update graph visualization
  useEffect(() => {
    // Create a new graph instance
    const graph = new Graph();

    // Add central node immediately
    graph.addNode(centralNode.id, {
      label: centralNode.label,
      size: 20,
      color: 'black',
      className: 'fade-in' // Apply the fade-in class
    });

    // Function to add nodes with delay
    const addNodesWithDelay = (nodes, delay, callback) => {
      nodes.forEach((node, index) => {
        setTimeout(() => {
          if (!graph.hasNode(node.id)) {
            graph.addNode(node.id, {
              label: node.label,
              size: 20,
              color: 'black',
              className: 'fade-in' // Apply the fade-in class
            });
          }
          // Call the callback after the last node is added
          if (index === nodes.length - 1 && callback) {
            callback();
          }
        }, index * delay);
      });
    };

    // Add parent nodes with delay
    addNodesWithDelay(parentNodes, 500, () => {
      // Position parent nodes after they are added
      const centerX = 0;
      const centerY = 0;
      const parentAngleSpread = Math.PI / 2;
      const parentRadius = 100;
      const parentStartAngle = 3 * Math.PI / 4;

      parentNodes.forEach((node, index) => {
        const angle = parentStartAngle + (index * parentAngleSpread) / Math.max(1, parentNodes.length - 1);
        const x = centerX + parentRadius * Math.cos(angle);
        const y = centerY + parentRadius * Math.sin(angle);
        graph.setNodeAttribute(node.id, 'x', x);
        graph.setNodeAttribute(node.id, 'y', y);
      });
    });

    // Add child nodes with delay
    addNodesWithDelay(childNodes, 500, () => {
      // Position child nodes after they are added
      const centerX = 0;
      const centerY = 0;
      const childAngleSpread = Math.PI / 2;
      const childRadius = 100;
      const childStartAngle = 7 * Math.PI / 4;

      childNodes.forEach((node, index) => {
        const angle = childStartAngle + (index * childAngleSpread) / Math.max(1, childNodes.length - 1);
        const x = centerX + childRadius * Math.cos(angle);
        const y = centerY + childRadius * Math.sin(angle);
        graph.setNodeAttribute(node.id, 'x', x);
        graph.setNodeAttribute(node.id, 'y', y);
      });
    });

    // Add edges after nodes are added
    setTimeout(() => {
      edges.forEach(edge => {
        if (!graph.hasEdge(edge.source, edge.target)) {
          try {
            graph.addEdge(edge.source, edge.target);
          } catch (error) {
            console.warn(`Failed to add edge from ${edge.source} to ${edge.target}:`, error);
          }
        }
      });
    }, Math.max(parentNodes.length, childNodes.length) * 500); // Ensure edges are added after nodes

    // Clear any existing click handlers
    sigma.removeAllListeners('clickNode');
    
    // Set up click handler
    sigma.on('clickNode', (event) => {
      handleNodeClick(event.node);
    });

    // Clear the existing graph and set the new one
    sigma.setGraph(graph);
    sigma.refresh();

    // Cleanup function
    return () => {
      sigma.removeAllListeners('clickNode');
    };
  }, [sigma, parentNodes, childNodes, edges, centralNode]);

  return <div ref={containerRef} />;
};

const GraphWrapper = () => (
  <SigmaContainer>
    <GraphComponent />
  </SigmaContainer>
);

export default GraphWrapper;
